import React, { useState } from "react";
import Navbar from "../components/navbar";
import "../styles/userPreferences.css";
import cinemaRoom from "../components/cinema_room.webp"; // Import the image

const UserPreferences = () => {
  const [rating, setRating] = useState(0);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [selectedMovies, setSelectedMovies] = useState([]);
  const [selectedYears, setSelectedYears] = useState([]);
  const [selectedAgeRatings, setSelectedAgeRatings] = useState([]);
  const [selectedThreshold, setSelectedThreshold] = useState([]);

  const [openDropdown, setOpenDropdown] = useState(null);

  // Dropdown options
  const genres = ["Adventure", "Action", "Animation", "Comedy", "Crime", "Drama", "Fantasy", "Horror", "Mystery", "Romance", "Sci-Fi", "Thriller", "Western"];
  const years = ["Last year", "Last 5 years", "Last 10 years", "Older"];
  const ageRatings = ["Teens(13+)", "Mature(16+)", "Adults(18+)", "All ages"];
  const ratingThresholds = ["1 star", "2 stars", "3 stars", "4 stars", "5 stars"];

  const handleRating = (index) => {
    setRating(index + 1);
  };

  // Handle dropdown selections
  const handleSelect = (option, state, setState, limit) => {
    setState((prev) => {
      if (prev.includes(option)) {
        return prev.filter((item) => item !== option);
      } else if (!limit || prev.length < limit) {
        return [...prev, option];
      }
      return prev;
    });
  };

  // Handle dropdown toggle
  const toggleDropdown = (dropdown) => {
    setOpenDropdown(openDropdown === dropdown ? null : dropdown);
  };

  return (
    <div className="page user-preferences">
      <Navbar />

      <div className="preferences-container">
        {/* 🔹 Left Section */}
        <div className="left-section">
          <img src={cinemaRoom} alt="Cinema Room" className="cinema-image" />
          <div className="review-box">
            <p className="rating-text">Rate us like a blockbuster movie - 5 stars, please!</p>
            <div className="stars">
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={`star ${index < rating ? "selected" : ""}`}
                  onClick={() => handleRating(index)}
                >
                  ★
                </span>
              ))}
            </div>
          </div>
          <button className="recommend-button">Recommend Movies</button>
        </div>

        {/* 🔹 Right Section */}
        <div className="preferences-right-section">
          <div className="preferences-title-section">
            <h2>WELCOME TO BINGE VIBE</h2>
            <p>Get ready to be recommended the best movies</p>
          </div>

          <div className="preferences-options-container">
            {/* 🔹 Select up to five preferred genres */}
            <div className="input-group custom-dropdown">
              <label>Select up to five preferred genres</label>
              <button className="dropdown-btn" onClick={() => toggleDropdown("genres")}>
                Choose options
              </button>
              {openDropdown === "genres" && (
                <div className="dropdown-content">
                  {genres.map((genre, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedGenres.includes(genre) ? "selected" : ""}`}
                      onClick={() => handleSelect(genre, selectedGenres, setSelectedGenres, 5)}
                    >
                      {genre}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Choose up to ten movies you have watched and enjoyed */}
            <div className="input-group custom-dropdown">
              <label>Choose up to ten movies you have watched and enjoyed</label>
              <button className="dropdown-btn" onClick={() => toggleDropdown("movies")}>
                Choose options
              </button>
              {openDropdown === "movies" && (
                <div className="dropdown-content">
                  {/* This will be dynamically generated */}
                </div>
              )}
            </div>

            {/* 🔹 Specify the release years */}
            <div className="input-group custom-dropdown">
              <label>Specify the release years</label>
              <button className="dropdown-btn" onClick={() => toggleDropdown("years")}>
                Choose options
              </button>
              {openDropdown === "years" && (
                <div className="dropdown-content">
                  {years.map((year, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedYears.includes(year) ? "selected" : ""}`}
                      onClick={() => handleSelect(year, selectedYears, setSelectedYears)}
                    >
                      {year}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Set an age rating preference */}
            <div className="input-group custom-dropdown">
              <label>Set an age rating preference</label>
              <button className="dropdown-btn" onClick={() => toggleDropdown("ageRatings")}>
                Choose options
              </button>
              {openDropdown === "ageRatings" && (
                <div className="dropdown-content">
                  {ageRatings.map((rating, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedAgeRatings.includes(rating) ? "selected" : ""}`}
                      onClick={() => handleSelect(rating, selectedAgeRatings, setSelectedAgeRatings)}
                    >
                      {rating}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Describe your ideal movie plot */}
            <div className="textarea-group">
              <label>Describe your ideal movie plot in a few sentences</label>
              <textarea placeholder="Enter text"></textarea>
            </div>

            {/* 🔹 Set a minimum rating threshold */}
            <div className="input-group custom-dropdown">
              <label>Set a minimum rating threshold</label>
              <button className="dropdown-btn" onClick={() => toggleDropdown("threshold")}>
                Choose options
              </button>
              {openDropdown === "threshold" && (
                <div className="dropdown-content">
                  {ratingThresholds.map((threshold, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedThreshold.includes(threshold) ? "selected" : ""}`}
                      onClick={() => handleSelect(threshold, selectedThreshold, setSelectedThreshold)}
                    >
                      {threshold}
                    </div>
                  ))}
                </div>
              )}
            </div>

          </div>
        </div>
      </div>
    </div>
  );
};

export default UserPreferences;
