import React, { useState } from "react";
import Navbar from "../components/navbar";
import "../styles/userPreferences.css";
import cinemaRoom from "../components/cinema_room.webp"; // Import the image

const UserPreferences = () => {
  const [rating, setRating] = useState(0);
  const [dropdowns, setDropdowns] = useState({
    genres: false,
    movies: false,
    years: false,
    ageRatings: false,
    ratingThresholds: false,
  });
  const [selectedOptions, setSelectedOptions] = useState({
    genres: [],
    movies: [],
    years: [],
    ageRatings: [],
    ratingThresholds: [],
  });

  // Dropdown options
  const genres = [
    "Adventure",
    "Action",
    "Animation",
    "Comedy",
    "Crime",
    "Drama",
    "Fantasy",
    "Horror",
    "Mystery",
    "Romance",
    "Sci-Fi",
    "Thriller",
    "Western",
  ];
  const years = ["Last year", "Last 5 years", "Last 10 years", "Older"];
  const ageRatings = ["Teens(13+)", "Mature(16+)", "Adults(18+)", "All ages"];
  const ratingThresholds = ["1 star", "2 stars", "3 stars", "4 stars", "5 stars"];

  // Function to handle rating selection
  const handleRating = (index) => {
    setRating(index + 1);
  };

  // Toggle dropdown visibility
  const toggleDropdown = (key) => {
    setDropdowns((prev) => ({
      ...Object.keys(prev).reduce((acc, item) => {
        acc[item] = item === key ? !prev[item] : false; // Ensures only one dropdown is open at a time
        return acc;
      }, {}),
    }));
  };

  // Handle selection in dropdown
  const handleSelection = (key, value, maxSelections) => {
    setSelectedOptions((prev) => {
      const updatedSelections = prev[key].includes(value)
        ? prev[key].filter((item) => item !== value) // Remove if already selected
        : prev[key].length < maxSelections
        ? [...prev[key], value] // Add if within limit
        : prev[key];

      return { ...prev, [key]: updatedSelections };
    });
  };

  return (
    <div className="page user-preferences">
      <Navbar />

      {/* Pink Container */}
      <div className="preferences-container">
        
        {/* ðŸ”¹ Left Section: Image, Review Box, Button */}
        <div className="left-section">
          <img src={cinemaRoom} alt="Cinema Room" className="cinema-image" />
          
          {/* ðŸ”¹ Only the review text and stars inside faded box */}
          <div className="review-box">
            <p className="rating-text">Rate us like a blockbuster movie - 5 stars, please!</p>
            <div className="stars">
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={`star ${index < rating ? "selected" : ""}`}
                  onClick={() => handleRating(index)}
                >
                  â˜…
                </span>
              ))}
            </div>
          </div>

          <button className="recommend-button">Recommend Movies</button>
        </div>

        {/* ðŸ”¹ Right Section */}
        <div className="preferences-right-section">
          
          {/* ðŸ”¹ Title Section */}
          <div className="preferences-title-section">
            <h2>WELCOME TO BINGE VIBE</h2>
            <p>Get ready to be recommended the best movies</p>
          </div>

          {/* ðŸ”¹ Options Section inside a faded black box */}
          <div className="preferences-options-container">

            {/* ðŸ”¹ Select up to five preferred genres */}
            <div className="input-group">
              <label>Select up to five preferred genres</label>
              <div className="custom-dropdown">
                <button className="dropdown-btn" onClick={() => toggleDropdown("genres")}>
                  Choose options
                </button>
                {dropdowns.genres && (
                  <div className="dropdown-content">
                    {genres.map((genre) => (
                      <div
                        key={genre}
                        className={`dropdown-item ${selectedOptions.genres.includes(genre) ? "selected" : ""}`}
                        onClick={() => handleSelection("genres", genre, 5)}
                      >
                        {genre}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* ðŸ”¹ Choose up to ten movies you have watched and enjoyed */}
            <div className="input-group">
              <label>Choose up to ten movies you have watched and enjoyed</label>
              <div className="custom-dropdown">
                <button className="dropdown-btn" onClick={() => toggleDropdown("movies")}>
                  Choose options
                </button>
                {dropdowns.movies && (
                  <div className="dropdown-content">
                    {/* Future dynamically generated options */}
                  </div>
                )}
              </div>
            </div>

            {/* ðŸ”¹ Specify the release years */}
            <div className="input-group">
              <label>Specify the release years</label>
              <div className="custom-dropdown">
                <button className="dropdown-btn" onClick={() => toggleDropdown("years")}>
                  Choose options
                </button>
                {dropdowns.years && (
                  <div className="dropdown-content">
                    {years.map((year) => (
                      <div
                        key={year}
                        className={`dropdown-item ${selectedOptions.years.includes(year) ? "selected" : ""}`}
                        onClick={() => handleSelection("years", year, 10)}
                      >
                        {year}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* ðŸ”¹ Set an age rating preference */}
            <div className="input-group">
              <label>Set an age rating preference</label>
              <div className="custom-dropdown">
                <button className="dropdown-btn" onClick={() => toggleDropdown("ageRatings")}>
                  Choose options
                </button>
                {dropdowns.ageRatings && (
                  <div className="dropdown-content">
                    {ageRatings.map((rating) => (
                      <div
                        key={rating}
                        className={`dropdown-item ${selectedOptions.ageRatings.includes(rating) ? "selected" : ""}`}
                        onClick={() => handleSelection("ageRatings", rating, 10)}
                      >
                        {rating}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

            {/* ðŸ”¹ Describe your ideal movie plot */}
            <div className="textarea-group">
              <label>Describe your ideal movie plot in a few sentences</label>
              <textarea placeholder="Enter text"></textarea>
            </div>

            {/* ðŸ”¹ Set a minimum rating threshold */}
            <div className="input-group">
              <label>Set a minimum rating threshold</label>
              <div className="custom-dropdown">
                <button className="dropdown-btn" onClick={() => toggleDropdown("ratingThresholds")}>
                  Choose options
                </button>
                {dropdowns.ratingThresholds && (
                  <div className="dropdown-content">
                    {ratingThresholds.map((threshold) => (
                      <div
                        key={threshold}
                        className={`dropdown-item ${selectedOptions.ratingThresholds.includes(threshold) ? "selected" : ""}`}
                        onClick={() => handleSelection("ratingThresholds", threshold, 10)}
                      >
                        {threshold}
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>

          </div>

        </div>

      </div>
    </div>
  );
};

export default UserPreferences;
