import React from "react";
import { Link } from "react-router-dom";
import "../styles/navbar.css"; // Import the CSS file for styles
import personIcon from "../components/person_icon.svg"; // Profile icon

const Navbar = ({ isAuthenticated, userData }) => {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const toggleSidebar = () => setSidebarOpen(!sidebarOpen);
  const closeSidebar = () => setSidebarOpen(false);

  return (
    <nav className="navbar">
      {/* Your other navbar content */}

      <div className="profile-container">
        <div className="profile-icon" onClick={toggleSidebar}>
          <img src={personIcon} alt="Profile" />
        </div>

        <div className={`profile-sidebar ${sidebarOpen ? 'active' : ''}`}>
          <div className="sidebar-content">
            {isAuthenticated ? (
              <div className="user-profile">
                <img src={userData.avatar || personIcon} alt="User" className="user-avatar" />
                <h3>{userData.name}</h3>
                <p>{userData.email}</p>
                <Link to="/profile" className="profile-btn" onClick={closeSidebar}>
                  View Profile
                </Link>
                <button className="profile-btn logout">Log Out</button>
              </div>
            ) : (
              <div className="auth-options">
                <h3>Welcome</h3>
                <Link to="/login" className="auth-btn" onClick={closeSidebar}>
                  Log In
                </Link>
                <div className="divider">or</div>
                <Link to="/register" className="auth-btn" onClick={closeSidebar}>
                  Register
                </Link>
              </div>
            )}
          </div>
          <button className="sidebar-close" onClick={closeSidebar}>
            Ã—
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
