import React from "react";
import Navbar from "../components/navbar";
import "../styles/profile.css"; // Profile styles
import backgroundImage from "../components/background_profile.webp"; // Profile background
import profileBox from "../components/profile_box.webp"; // Profile container background
import userImage from "../components/user_avatar.webp"; // User's profile picture placeholder

// Dummy user data (replace with real data from backend)
const userData = {
  name: "John Doe",
  email: "john.doe@example.com",
  joined: "January 2024",
  likedMovies: [
    {
      title: "Inception",
      poster: "https://image.tmdb.org/t/p/w500/qmDpIHrmpJINaRKAfWQfftjCdyi.jpg",
    },
    {
      title: "Interstellar",
      poster: "https://image.tmdb.org/t/p/w500/gEU2QniE6E77NI6lCU6MxlNBvIx.jpg",
    },
    {
      title: "The Dark Knight",
      poster: "https://image.tmdb.org/t/p/w500/qJ2tW6WMUDux911r6m7haRef0WH.jpg",
    },
    {
      title: "The Matrix",
      poster: "https://image.tmdb.org/t/p/w500/f89U3ADr1oiB1s9GkdPOEpXUk5H.jpg",
    },
    {
      title: "Gladiator",
      poster: "https://image.tmdb.org/t/p/w500/pPJeHknEjAQmpaRzdTTRXfFmghn.jpg",
    },
    {
      title: "Avatar",
      poster: "https://image.tmdb.org/t/p/w500/6EiRUJpuoeQPghrs3YNktfnqOVh.jpg",
    },
  ],
};

const Profile = () => {
  return (
    <div
      className="page profile"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <Navbar />
      
      <div className="profile-container" style={{ backgroundImage: `url(${profileBox})` }}>
        
        {/* Profile Header */}
        <div className="profile-header">
          <img src={userImage} alt="User Avatar" className="profile-avatar" />
          <div className="profile-info">
            <h2>{userData.name}</h2>
            <p>Email: {userData.email}</p>
            <p>Joined: {userData.joined}</p>
          </div>
        </div>

        {/* Liked Movies Section */}
        <div className="liked-movies-section">
          <h3>Movies You Liked</h3>
          {userData.likedMovies.length > 0 ? (
            <div className="movies-list">
              <button className="scroll-btn left-btn">{"<"}</button>
              <div className="movies-container">
                {userData.likedMovies.map((movie, index) => (
                  <div key={index} className="movie-card">
                    <img src={movie.poster} alt={movie.title} />
                    <p>{movie.title}</p>
                  </div>
                ))}
              </div>
              <button className="scroll-btn right-btn">{">"}</button>
            </div>
          ) : (
            <p className="no-movies-message">
              You havenâ€™t liked any movies yet! Start exploring and hit the like button!
            </p>
          )}
        </div>
      </div>
    </div>
  );
};

export default Profile;
