import React, { useState } from "react";
import Navbar from "../components/navbar";
import "../styles/userPreferences.css";
import cinemaRoom from "../components/cinema_room.webp"; // Import the image

const UserPreferences = () => {
  const [rating, setRating] = useState(0);
  const [selectedGenres, setSelectedGenres] = useState([]);
  const [selectedMovies, setSelectedMovies] = useState([]);
  const [selectedYears, setSelectedYears] = useState([]);
  const [selectedAgeRatings, setSelectedAgeRatings] = useState([]);
  const [selectedThresholds, setSelectedThresholds] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState({
    genres: false,
    movies: false,
    years: false,
    ageRatings: false,
    thresholds: false,
  });

  // Dropdown options
  const genres = [
    "Adventure",
    "Action",
    "Animation",
    "Comedy",
    "Crime",
    "Drama",
    "Fantasy",
    "Horror",
    "Mystery",
    "Romance",
    "Sci-Fi",
    "Thriller",
    "Western",
  ];
  const years = ["Last year", "Last 5 years", "Last 10 years", "Older"];
  const ageRatings = ["Teens(13+)", "Mature(16+)", "Adults(18+)", "All ages"];
  const ratingThresholds = ["1 star", "2 stars", "3 stars", "4 stars", "5 stars"];

  const handleRating = (index) => {
    setRating(index + 1);
  };

  // Handles selection of dropdown items
  const handleSelect = (option, setSelected, selected, maxLimit) => {
    if (selected.includes(option)) {
      setSelected(selected.filter((item) => item !== option));
    } else if (selected.length < maxLimit || maxLimit === undefined) {
      setSelected([...selected, option]);
    }
  };

  return (
    <div className="page user-preferences">
      <Navbar />

      {/* Pink Container */}
      <div className="preferences-container">
        {/* 🔹 Left Section: Image, Review Box, Button */}
        <div className="left-section">
          <img src={cinemaRoom} alt="Cinema Room" className="cinema-image" />

          {/* 🔹 Only the review text and stars inside faded box */}
          <div className="review-box">
            <p className="rating-text">Rate us like a blockbuster movie - 5 stars, please!</p>
            <div className="stars">
              {[...Array(5)].map((_, index) => (
                <span
                  key={index}
                  className={`star ${index < rating ? "selected" : ""}`}
                  onClick={() => handleRating(index)}
                >
                  ★
                </span>
              ))}
            </div>
          </div>

          <button className="recommend-button">Recommend Movies</button>
        </div>

        {/* 🔹 Right Section */}
        <div className="preferences-right-section">
          {/* 🔹 Title Section */}
          <div className="preferences-title-section">
            <h2>WELCOME TO BINGE VIBE</h2>
            <p>Get ready to be recommended the best movies</p>
          </div>

          {/* 🔹 Options Section inside a faded black box */}
          <div className="preferences-options-container">
            {/* 🔹 Select up to five preferred genres */}
            <div className="input-group">
              <label>Select up to five preferred genres</label>
              <div
                className="dropdown"
                onClick={() => setDropdownOpen({ ...dropdownOpen, genres: !dropdownOpen.genres })}
              >
                Choose options
              </div>
              {dropdownOpen.genres && (
                <div className="dropdown-options">
                  {genres.map((genre, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedGenres.includes(genre) ? "selected" : ""}`}
                      onClick={() => handleSelect(genre, setSelectedGenres, selectedGenres, 5)}
                    >
                      {genre}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Choose up to ten movies you have watched and enjoyed (Generated later) */}
            <div className="input-group">
              <label>Choose up to ten movies you have watched and enjoyed</label>
              <div
                className="dropdown"
                onClick={() => setDropdownOpen({ ...dropdownOpen, movies: !dropdownOpen.movies })}
              >
                Choose options
              </div>
              {dropdownOpen.movies && (
                <div className="dropdown-options">
                  {/* Placeholder for dynamically generated movies */}
                </div>
              )}
            </div>

            {/* 🔹 Specify the release years */}
            <div className="input-group">
              <label>Specify the release years</label>
              <div
                className="dropdown"
                onClick={() => setDropdownOpen({ ...dropdownOpen, years: !dropdownOpen.years })}
              >
                Choose options
              </div>
              {dropdownOpen.years && (
                <div className="dropdown-options">
                  {years.map((year, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedYears.includes(year) ? "selected" : ""}`}
                      onClick={() => handleSelect(year, setSelectedYears, selectedYears)}
                    >
                      {year}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Set an age rating preference */}
            <div className="input-group">
              <label>Set an age rating preference</label>
              <div
                className="dropdown"
                onClick={() => setDropdownOpen({ ...dropdownOpen, ageRatings: !dropdownOpen.ageRatings })}
              >
                Choose options
              </div>
              {dropdownOpen.ageRatings && (
                <div className="dropdown-options">
                  {ageRatings.map((rating, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedAgeRatings.includes(rating) ? "selected" : ""}`}
                      onClick={() => handleSelect(rating, setSelectedAgeRatings, selectedAgeRatings)}
                    >
                      {rating}
                    </div>
                  ))}
                </div>
              )}
            </div>

            {/* 🔹 Describe your ideal movie plot */}
            <div className="textarea-group">
              <label>Describe your ideal movie plot in a few sentences</label>
              <textarea placeholder="Enter text"></textarea>
            </div>

            {/* 🔹 Set a minimum rating threshold */}
            <div className="input-group">
              <label>Set a minimum rating threshold</label>
              <div
                className="dropdown"
                onClick={() => setDropdownOpen({ ...dropdownOpen, thresholds: !dropdownOpen.thresholds })}
              >
                Choose options
              </div>
              {dropdownOpen.thresholds && (
                <div className="dropdown-options">
                  {ratingThresholds.map((threshold, index) => (
                    <div
                      key={index}
                      className={`dropdown-item ${selectedThresholds.includes(threshold) ? "selected" : ""}`}
                      onClick={() => handleSelect(threshold, setSelectedThresholds, selectedThresholds)}
                    >
                      {threshold}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserPreferences;
