import React, { useState } from "react";
import Navbar from "../components/navbar";
import "../styles/userPreferences.css";

const genres = [
  "Adventure", "Action", "Animation", "Comedy", "Crime", "Drama", "Fantasy", "Horror", "Mystery", "Romance", "Sci-Fi", "Thriller", "Western"
];

const years = ["Last year", "Last 5 years", "Last 10 years", "Older"];
const ages = ["Teens (13+)", "Mature (16+)", "Adults (18+)", "All ages"];
const stars = ["1 star", "2 stars", "3 stars", "4 stars", "5 stars"];

const MultiSelectDropdown = ({ options, maxSelection }) => {
  const [selectedOptions, setSelectedOptions] = useState([]);
  const [isOpen, setIsOpen] = useState(false);

  const toggleDropdown = () => {
    setIsOpen(!isOpen);
  };

  const handleSelect = (option) => {
    if (selectedOptions.includes(option)) {
      setSelectedOptions(selectedOptions.filter((item) => item !== option));
    } else if (selectedOptions.length < maxSelection || !maxSelection) {
      setSelectedOptions([...selectedOptions, option]);
    }
  };

  return (
    <div className="custom-dropdown">
      <button className="dropdown-button" onClick={toggleDropdown}>Choose options</button>
      {isOpen && (
        <div className="dropdown-options">
          {options.map((option, index) => (
            <div
              key={index}
              className={`dropdown-option ${selectedOptions.includes(option) ? "selected" : ""}`}
              onClick={() => handleSelect(option)}
            >
              {option}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

const UserPreferences = () => {
  return (
    <div className="page user-preferences">
      <Navbar />
      <div className="preferences-container">
        <div className="preferences-right-section">
          <div className="preferences-title-section">
            <h2>WELCOME TO BINGE VIBE</h2>
            <p>Get ready to be recommended the best movies</p>
          </div>

          <div className="preferences-options-container">
            <div className="input-group">
              <label>Select up to five preferred genres</label>
              <MultiSelectDropdown options={genres} maxSelection={5} />
            </div>

            <div className="input-group">
              <label>Choose up to ten movies you have watched and enjoyed</label>
              <MultiSelectDropdown options={[]} maxSelection={10} />
            </div>

            <div className="input-group">
              <label>Specify the release years</label>
              <MultiSelectDropdown options={years} maxSelection={null} />
            </div>

            <div className="input-group">
              <label>Set an age rating preference</label>
              <MultiSelectDropdown options={ages} maxSelection={null} />
            </div>

            <div className="input-group">
              <label>Set a minimum rating threshold</label>
              <MultiSelectDropdown options={stars} maxSelection={null} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserPreferences;
